// mockjs
const Mock = require('mockjs');
const qs = require('qs');
//  mock.Random 
const Random = Mock.Random;

// 
const login = function() {
  let status = false;//ture--false--
  status = true;
  return {
    status: status
  }
}
// 
const tblList = function(param) {
  param = param.body || '';//
  // console.log(qs.parse(param));//axiospostqs.stringfy()
	if(param == ''){
	  //
    let bookdata = Mock.mock({
      'books|1-10': [{
        'sid|+1': 1,
        'date': '@date("yyyy-MM-dd")',
        'name': '@csentence()',
        'address': '@county(true)',
        'region': ''
      }]
    });
    // console.log(bookdata);
    return bookdata;

  }else{
	  //
    let paramObj = qs.parse(param);
    console.log('input parameter...');
    console.log(paramObj);
    let books = [];
    for (let i = 0; i < 10; i++) {
      let newBook = {
        // thumbnail_pic_s: Random.dataImage('300x250', 'mock'), // Random.dataImage( size, text )  Base64 
        date: Random.date(),
        name: paramObj.name+Random.csentence(),
        address: Random.county(true),
        region: paramObj.region
      }
      books.push(newBook)
    }
    return {
      books: books
    }
  }

}
//
const bookList = function(param) {
  param = param.body || '';//
  // console.log(qs.parse(param));
  // axiospostqs.stringfy()
  let paramObj = qs.parse(param);
  console.log(paramObj);
  let nameInput = paramObj.name || '';
  let books = [];
  for (let i = 0; i < 10; i++) {
    let temp = i+1;
    let newBook = {
      sid: temp,
      // thumbnail_pic_s: Random.dataImage('300x250', 'mock'), // Random.dataImage( size, text )  Base64 
      author: Random.name(2,4),
      name: ''+nameInput+Random.csentence()+"",
      publishAt: Random.date(),
      description: Random.cparagraph()
    }
    books.push(newBook)
  }
  return {
    books: books
  }
}
//
const fileList = function (param) {
  param = param.body || '';//
  // console.log(qs.parse(param));
  // axiospostqs.stringfy()
  let paramObj = qs.parse(param);
  console.log(paramObj);
  let files = [];
  for (let i = 0; i < 3; i++) {
    let newFile = {
      name: paramObj.username + i +'.png',
      url: Random.dataImage('300x250', 'mock'), // Random.dataImage( size, text )  Base64 
    }
    files.push(newFile)
  }
  return {
    fileList: files
  }
}

// Mock.mock( url, post/get , )
Mock.mock('/mk_login', 'post', login);// 
Mock.mock('/mk_tbllist', 'post', tblList);// 
Mock.mock('/mk_booklist', 'post',bookList);// 
Mock.mock('/mk_fileupload', 'post',{});// 
Mock.mock('/mk_fetchFiles', 'post',fileList);// 
```
